      .icon {
        font-size: 1.75rem;
        color: var(--color-accent, rebeccapurple);
        transition: var(--transition, 0.1s transform);
      }

      &:hover .icon {
        transform: scale(1.1);
      }
    }
  }
}

form {
  display: grid;
  gap: 1rem;
  padding: 2rem;
  border: 1px solid var(--border-color, #eee);
  border-radius: var(--border-radius);

  label {
    display: grid;
    gap: 0.25rem;

    span {
      font-size: 0.75rem;
      font-weight: 500;
    }

    input,
    textarea {
      font-size: 1rem;
